mapper.select:
SELECT T1.ORDER_NO, T1.CUST_ID, T1.ORDER_STAT_CD, T1.ORDER_DATE, T1.REG_DATE, T1.USER_POINT_INFO, T1.ORDER_TRANSFER_NO, T1.ORDER_TRANSFER
FROM BOM_ORDER_TB T1 WHERE (1 = 1) AND T1.CUST_ID = #{customerInfo.custId} AND T1.ORDER_NO = #{orderNo} AND T1.REG_DATE BETWEEN #{srchStdDt} AND #{srchEdDt}
ORDER BY T1.REG_DATE DESC

SELECT T1.HM_ORDER_NO, T1.HM_ITEM_CD, T1.HM_ORDER_GB, T1.HM_ORDER_DM, T1.HM_SHOP_NO, T1.HM_SHOP_EVENT_NO, T1.HM_MEM_NO, T1.HM_TAX_GB, T1.HM_SALE_PRICE, T1.HM_POINT_AMT, T1.HM_ETC_AMT, T1.HM_MEDIA_CD, T1.HM_DELI_AMT, T1.HM_ITEM_NM, T1.HM_ITEM_PRICE, T1.HM_ORDER_QTY, T1.HM_DC_PRICE, T1.HM_RETURN_CODE
FROM BOM_HM_TB T1 WHERE (1 = 1) AND T1.HM_ORDER_DM BETWEEN #{srchStdDt} AND #{srchEdDt}
ORDER BY T1.HM_ORDER_DM DESC
